import {  StyleSheet } from 'react-native'

const styles = StyleSheet.create({

    container: {
		flex: 1,
		justifyContent: "center",
		alignItems: "center",
		backgroundColor: "#303030"
	},

    grafico: {
		
		borderRadius: 5,
		alignItems: "center",
		backgroundColor: "#fff",
		width: "80%",
		height: "60%",
	},

	title: {
		fontSize: 20,
		fontWeight: "bold",
		margin: 10,
		color:"#fff",
	},

	timeWrapper: {
		flexDirection: "row",
		flexWrap: "wrap",
		justifyContent: "space-between",
        borderRadius: 5,
		backgroundColor:"#232323", //cor de fundo da sele√ßao de dias
		width: "78%",
	},

	coins: {
		flexDirection: "row",
		flexWrap: "wrap",
		justifyContent: "space-between"
	},

	time: {
		margin: 10,
		color:"#fff",//cor do texto
		fontSize: 20,//tamanho da fonte 
		fontWeight:'bold',//cor em negrito
	},

	header: {
		position: "absolute",
		top: 50,
		fontSize: 25,
		fontWeight: "bold",
		color:"#f7931a",
	},

	underline: { 
	
	//quando for selecioando vai ficar assim
	textDecorationLine: "underline",
	color:"#f7931a",

    }
})

export default styles
