import { View } from 'react-native'
import  React from 'react'

//importando a lib das animacoes
import LottieView from 'lottie-react-native'

//importando o json com as amimacoes
import blogAnimation from './assets/blog.json'

export default function Animation_blog() {
    
  //aqui fica o titulo do app

    return (
      
    <View>
     <LottieView
         
         source={blogAnimation}//caminho do json
         autoplay loop//play automatico
         loop={true}//loop infinito

         style={{
           width: 300,
           height: 300

         }}

         resizeMode="cover"

      />
    </View>

    )
  }
